    z-index: 1000;
  }

  /* Ensure hamburger menu container is visible */
  .hamburger-menu {
    display: block !important;
    position: relative;
  }

  /* Force three dots to show */
  .three-dots {
    display: flex !important;
    visibility: visible !important;
    opacity: 1 !important;
    cursor: pointer;
    padding: 0.8rem;
    border-radius: 8px;
    flex-direction: column;
    gap: 0.4rem;
    align-items: center;
    z-index: 200;
    transition: transform 0.3s ease;
    background: rgba(41, 217, 213, 0.1);
    min-width: 40px;
    min-height: 40px;
  }

  .three-dots:hover {
    background: rgba(41, 217, 213, 0.2);
  }

  /* Make dots more visible */
